    //类型后面加?表示可为空
    //var age: String? = "aa"
   // var age: String? = "1"
    var age: String? = null
     //抛出空指针异常
    try {
        val ages = age!!.toInt()
        println("ages:"+ages)
    }catch (e : KotlinNullPointerException){
        println("parse has exception ")
    }
    //不做处理返回 null
